// Code node setelah AI Agent
const raw = $input.first().json.output;
const clean = raw.replace(/```json|```/g, '').trim();
const parsed = JSON.parse(clean);
return [{ json: parsed }];

const parsed = $('parsing json').first().json;
const intent = parsed.intent;

if (intent === 'upsert') {
  return [
    {
      json: {
        stock: parsed.sql_stocks,
        stock_opname: parsed.sql_stock_opname
      }
    }
  ];
}

return [];

let message = null;

if (!$('parsing json').first().json.valid){
  message=$input.first().json.reason;
}else{
  const intent = $('parsing json').first().json.intent;
  switch (intent) {
    case 'upsert':
      const parsed = $('parsing json').first().json;
      const activity = $('parsing json').first().json.activity;
      switch (activity) {
        case 'created':
          message = `Produk '${parsed.product_name}' di kategori '${parsed.category}' dengan stok ${parsed.stock} berhasil ditambahkan.`;
          break;
        case 'updated':
          message = `Produk '${parsed.product_name}' di kategori '${parsed.category}' berhasil diupdate stok-nya menjadi ${parsed.stock}.`;
          break;
      }
      break;

    case 'check_stock': {
      const input = $input.first().json || {};
      message = `Saat ini stok produk '${input.product_name}' di kategori '${input.category}' adalah ${input.stock}.`;
      break;
    }

    case 'find': {
      const query = $('stock').first().json.query || "";
      const category = $input.first().json.category || "";
      if (!query.toLowerCase().includes('product') && category !== "") {
        message = `Kategori '${category}' ditemukan.`;
      }else if (query.toLowerCase().includes('product')) {
        const productName = $input.first().json.product_name || "";
        const stock = $input.first().json.stock || "";
        message = `Produk '${productName}' ditemukan dengan stock '${stock}'`;
      } else {
        message = "Tidak ada produk atau kategori yang ditemukan.";
      }
      break;
    }

    case 'list': {
      const query = $('stock').first().json.query || "";
      const category = $input.first().json.category || "";
      if (!query.toLowerCase().includes('product') && category !== "") {
        const categories = $input.all().map(item => `'${item.json.category}'`).join(", ");
        message = `Berikut List Kategori  yang tersedia: '${categories}'`;
      }else if (query.toLowerCase().includes('product')) {
        const products = $input.all().map(item => item.json.product_name).join(", ");
        message = `Berikut List Produk yang tersedia: '${products}'`;
      } else {
        message = "Tidak ada produk atau kategori yang ditemukan.";
      }
      break;
    }

    case 'sort': {
      const grouped = {};
      const query = $('stock').first().json.query || "";
      if (!query.toLowerCase().includes('stock') && !query.toLowerCase().includes('updated_at')) {
        message = "Tidak ada pengurutan stok atau waktu update yang ditemukan.";
      } else if (!query.toLowerCase().includes('updated_at')) {
        // Grouping berdasarkan kategori dan sort berdasarkan stock
        $input.all().forEach(item => {
          const { category, product_name, stock } = item.json;
          if (!grouped[category]) grouped[category] = [];
          grouped[category].push({ product_name, stock: Number(stock) });
        });

        for (const category in grouped) {
          grouped[category].sort((a, b) => {
            return query.toLowerCase().includes('asc')
              ? a.stock - b.stock
              : b.stock - a.stock;
          });
        }

        // Formatting
        let formatted = "";
        for (const category in grouped) {
          formatted += `\nKategori *${category}*:\n`;
          grouped[category].forEach(item => {
            formatted += `â€¢ ${item.product_name}: ${item.stock}\n`;
          });
        }

        message = query.toLowerCase().includes('asc')
          ? `Stok produk telah diurutkan dari yang paling sedikit:${formatted}`
          : `Stok produk telah diurutkan dari yang paling banyak:${formatted}`;

      } else if (query.toLowerCase().includes('updated_at')) {
        // Sorting seluruh produk berdasarkan waktu update
        const sorted = $input.all()
          .map(item => {
            const { category, product_name, stock, updated_at } = item.json;
            return { category, product_name, stock: Number(stock), updated_at };
          })
          .sort((a, b) => {
            return query.toLowerCase().includes('asc')
              ? new Date(a.updated_at) - new Date(b.updated_at)
              : new Date(b.updated_at) - new Date(a.updated_at);
          });

        // Formatting
        let formatted = "";
        sorted.forEach(item => {
          const updated = new Date(item.updated_at).toLocaleString("id-ID", {
            timeZone: "Asia/Jakarta",
            day: "2-digit", month: "2-digit", year: "numeric",
            hour: "2-digit", minute: "2-digit", second: "2-digit"
          });
          formatted += `â€¢ ${item.product_name} (${item.category}) - Stok: ${item.stock}, Terakhir diupdate: ${updated}\n`;
        });

        message = query.toLowerCase().includes('asc')
          ? `Stok produk telah diurutkan dari yang paling lama:\n${formatted}`
          : `Stok produk telah diurutkan dari yang paling baru:\n${formatted}`;
      }

      break;
    }

    case 'last_transactions': {
      const transactions = $input.all()
        .map(item => {
          const { category, product_name, stock, by_user, created_at, activity } = item.json;
          return {
            category,
            product_name,
            stock: Number(stock),
            by_user,
            created_at,
            activity
          };
        })
        .sort((a, b) => new Date(b.created_at) - new Date(a.created_at)); // urutkan terbaru dulu

      if (transactions.length === 0) {
        message = "Belum ada transaksi yang tercatat.";
        break;
      }

      message = `ðŸ“¦ Berikut transaksi terakhir:\n`;

      transactions.forEach((tx, index) => {
        const time = new Date(tx.created_at).toLocaleString("id-ID", {
          timeZone: "Asia/Jakarta",
          day: "2-digit", month: "2-digit", year: "numeric",
          hour: "2-digit", minute: "2-digit", second: "2-digit"
        });
        message += `\n${index + 1}. [${tx.activity.toUpperCase()}] Produk *${tx.product_name}* (Kategori: ${tx.category}) â†’ Stok: ${tx.stock}, oleh ${tx.by_user} pada ${time}`;
      });
      break;
    }
  }

}



if (message) {
  return [{
    json: {
      intent:$('parsing json').first().json.intent,
      user:$('parsing json').first().json.user,
      response: message
    }
  }];
}

return [];

